ValidationRule.errorConditionFormula:
AND( ISCHANGED(StageName) ,OR(AND(
ISPICKVAL(StageName, "Calificada Presupuestal"),ISPICKVAL(PRIORVALUE(StageName), "Perdida - Presupuestal")
),AND(
ISPICKVAL(StageName, "Generada Presupuestal"),ISPICKVAL(PRIORVALUE(StageName), "Perdida - Presupuestal")
), AND(
ISPICKVAL(StageName, "Ofertada Presupuestal"),ISPICKVAL(PRIORVALUE(StageName), "Perdida - Presupuestal")
),AND(
ISPICKVAL(StageName, "Identificada Presupuestal"),ISPICKVAL(PRIORVALUE(StageName), "Perdida - Presupuestal")
) ))
